mapper.select:
/* sql_id : readNtcMttrList */ select org_cd as orgCd , bltn_no as bltnNo , bltn_dvs_cd as bltnDvsCd , tpw_bltn_rgt_dtm as tpwBltnRgtDtm , ntc_mttr_ttl_nm as ntcMttrTtlNm , main_exps_yn as mainExpsYn , inqr_ncnt as inqrNcnt , rgsr_id as rgsrId , rgt_dtm as rgtDtm from ohxzown.tbhxzm113 where 1=1
and lower(ntc_mttr_ttl_nm) like concat('%', lower(#{req.ntcMttrTtlNm}), '%')
and lower(ntc_mttr_ctt) like concat('%', lower(#{req.ntcMttrCtt}), '%')
order by #{req.sort} ${req.dir} limit #{req.size} offset #{req.page}

/* sql_id : readNtcMttrListCnt */ select count(1) from tbhxzm113 where 1 = 1
and lower(ntc_mttr_ttl_nm) like concat('%', lower(#{req.ntcMttrTtlNm}), '%')
and lower(ntc_mttr_ctt) like concat('%', lower(#{req.ntcMttrCtt}), '%')
